b(\"{quantity}\", quantity)\nend\n\n\nfunction updateScreen()\n    local ores = HTMLTable()\n\n    ores.addRow({\"Bauxite / Aluminium\", progressBar(bauxite_hub, true), progressBar(aluminium_hub), \"\"})\n    ores.addRow({\"Coal / Carbon\", progressBar(coal_hub, true), progressBar(carbon_hub), \"\"})\n    ores.addRow({\"Hematite / Iron\", progressBar(hematite_hub, true), progressBar(iron_hub), \"\"})\n    ores.addRow({\"Quartz / Silicon\", progressBar(quartz_hub, true), progressBar(silicon_hub), \"\"})\n\n    myscreen.update('<div class=\"header\">Tier 1</div>' .. ores.render())\nend\n\nunit.hide()\nupdateScreen()","filter":{"args":[],"signature":"start()","slotKey":"-1"},"key":"0"},{"code":"updateScreen()","filter":{"args":[{"value":"refreshScreen"}],"signature":"tick(timerId)","slotKey":"-1"},"key":"1"},{"code":"function formatLitre(value)\n    if value > 1000 then\n        return (math.floor(value / 10) / 100) .. \"kL\"\n    else\n        return value .. \"L\"\n    end\nend\n\nfunction Container(_name)\n    local self = {\n        density = 0,\n        container = false,\n        mass = 0,\n        size = 0,\n        initialized = true,\n        name = \"\"\n    }\n    \n    self.container = getNamedElement(\"itemcontainer\",_name)\n   \n    if self.container == false then\n        system.logError(\"ERROR: Cannot find \" .. _name .. \". Please ensure you link a container to this keyboars with that name.\")\n        system.print(\"ERROR: Cannot find \" .. _name .. \". Please ensure you link a container to this keyboars with that name.\")\n        initialized = false\n    end\n    \n    if OreStructure[_name] then\n        self.density = OreStructure[_name].density\n        self.mass =  function() return self.container.getItemsMass() end\n        self.size = self.container.getMaxVolume()\n        self.name = _name\n        if self.density == nil then system.print(\"Container: \" .. _name .. \" density is nil\") end\n        if self.mass() < 1 then system.print(\"ERROR: Container: \" .. _name .. \" mass=\" .. self.mass()) end\n    else\n        system.logError(\"ERROR: Cannot find \" .. _name .. \" in OreStructure\")\n        system.print(\"ERROR: Cannot find \" .. _name .. \" in OreStructure\")\n        initialized = false\n    end\n    \n    if self.initialized == false then\n        return\n    end\n        \n    \n    if gDebug == true then system.print(\"Container: \" .. _name .. \" density=\" .. self.density .. \", mass=\" .. self.mass .. \", size=\" .. self.size) end\n\n    function self.getQuantity(asNumber)\n        result = 0\n        system.print(\"Container(\" .. self.name .. \")-getQuantity density=\" .. self.density .. \", mass=\" .. self.mass())\n        if self.density > 0 then\n            result = math.floor(self.mass() / self.density)\n        else\n            system.print(\"You must set a density to the container\")\n        end\n\n        if asNumber then\n            return result\n        end\n        return formatLitre(result)\n    end\n\n    function self.getPercentage(asNumber)\n        if self.getSize() <= 0 then\n            system.print(\"You must set a size to container=\" .. self.name)\n            if asNumber then\n                return 0\n            else\n                return \"0%\"\n            end\n        end\n        local result = math.floor(self.getQuantity(true) / self.getSize() * 100)\n        \n        system.print(\"Container(\" .. self.name .. \")-getPercentage result=\" .. result .. \", quantity=\" .. self.getQuantity(true) .. \", size=\" .. self.getSize())\n\n        if asNumber then\n            return result\n        else\n            return result .. \"%\"\n        end\n    end\n\n    function self.getSize()\n        return self.size\n    end\n\n    function self.getMass()\n        return self.mass()\n    end\n\n    function self.getDensity()\n        return self.density\n    end\n    \n    function self.getName()\n        return self.name\n    end\n\n    function self.getData()\n        return {\n            density = self.getDensity(),\n            mass = self.getMass(),\n            size = self.getSize()\n        }\n    end\n\n    return self\nend\n\nfunction HTMLTableCell(data, isHeader, styles)\n    local self = {\n        isHeader = false,\n        data = {},\n        styles = \"\"\n    }\n    self.isHeader = isHeader or false\n    self.styles = styles\n    self.data = data\n\n    function self.render(colspan)\n        tag = \"td\"\n        if self.isHeader then\n            tag = \"th\"\n        end\n        return \"<\" .. tag .. \" colspan=\\\"\" .. colspan .. \"\\\" style=\\\"\" .. self.styles .. \"\\\">\" .. self.data .. \"</\" .. tag .. \">\"\n    end\n\n    return self\nend\n\nfunction HTMLTableRow(data, isHeader, styles)\n    local self = {\n        cells = {},\n        styles = \"\"\n    }\n\n    self.styles = styles or \"\"\n\n    for i=1,#data do\n        table.insert(self.cells, HTMLTableCell(data[i], isHeader, self.styles))\n    end\n\n\n    function self.render(tableCellCount)\n        cellCount = #self.cells\n\n        if cellCount == 0 then\n            return \"\"\n        end\n\n        colspan = math.floor(tableCellCount / cellCount)\n        span = colspan + tableCellCount - (cellCount * colspan)\n        result = \"<tr>\"\n        for i=1,#self.cells do\n            result = result .. self.cells[i].render(span)\n            span = colspan\n        end\n        return result .. \"</tr>\"\n    end\n\n    return self\nend\n\nfunction HTMLTable()\n    local self = {\n        cellCount = 0,\n        rows = {}\n    }\n\n    function self.addRow(data, isHeader, styles)\n        self.rows[#self.rows + 1] = HTMLTableRow(data, isHeader, styles)\n        if self.cellCount < #data then\n            self.cellCount = #data\n        end\n    end\n\n    function self.render()\n        result = \"<table>\"\n        for i=1,#self.rows do\n            result = result .. self.rows[i].render(self.cellCount)\n        end\n        return result .. \"</table>\"\n    end\n\n    return self\nend\n\nfunction Screen(display)\n    local self = {\n        styles = [[\n        body {\n        background: black;\n        color: white;\n        height: 100vh;\n        padding: 20px;\n        width: 100vw;\n    }\n        .header {\n        background: black;\n        color: white;\n        font-size: 8vh;\n        font-weight: 900;\n        margin: 5vh 0;\n        text-align: center;\n    }\n        table {\n        color: white;\n        font-size: 5vh;\n        width: calc(100% - 40px);\n        box-sizing: border-box;\n    }\n        table tr td:nth-of-type(1) {\n        font-size: 0.9em;\n        width: 20%;\n    }\n        table tr td:nth-of-type(2) {\n        width: 60%;\n    }\n        table tr td:nth-of-type(3) {\n        width: 20%;\n    }\n        table tr td {\n        border-bottom: 2px solid white;\n        font-family: verdana;\n        padding: 5px;\n    }\n        table tr:last-child td {\n        border-bottom: none;\n    }\n        table tr.flash {\n        color: black;\n        background: red;\n    }\n        .progress-bar {\n        background-color: black;\n        border: 2px solid white;\n        border-radius: 25px;\n        height: 10vh;\n        padding: 1vh;\n        position: relative;\n        width: 100%;\n    }\n        .percent{\n        font-size: 4vh;\n        left:0;\n        line-height: 10vh;\n        position: absolute;\n        text-align: center;\n        top: 0;\n        width: 100%;\n        z-index:1;\n    }\n        .progress-bar-show {\n        background-color: rgba(0,116,194,0.4);\n        border-radius: 30px;\n        display: block;\n        height: 100%;\n        overflow: hidden;\n        position: relative;\n    }\n        ]],\n        content = \"\",\n        display = function(content) end\n    }\n\n    function self.update(content)\n        self.setContent(content)\n        display(self.getContent())\n    end\n\n    function self.setContent(content)\n        self.content = content\n    end\n\n    function self.appendContent(content)\n        self.content = self.content .. content\n    end\n\n    function self.clear()\n        self.udpate(\"\")\n    end\n\n    function self.getContent()\n        return [[\n        <html>\n        <head>\n        <style>\n        ]]..self.styles..[[\n        </style>\n        </head>\n        <body>\n        <div class=\"screen\">\n        ]]..self.content..[[\n        </div>\n        </body>\n        </html>\n        ]]\n    end    \n\n    return self\nend","filter":{"args":[],"signature":"start()","slotKey":"-2"},"key":"2"},{"code":"gDebug = false --export\ngStructureInitialized = false\n\nfunction getCore()\n    for slot_name, slot in pairs(unit) do\n        if type(slot) == \"table\" and type(slot.export) == \"table\" and slot.getElementClass then\n            local elementClass = slot.getElementClass():lower()\n            if elementClass:find(\"coreunit\") then\n                system.print(\"getCore: found core\")\n                return slot\n            end\n        end\n    end\n    return false\nend\n\nfunction getScreen()\n    for slot_name, slot in pairs(unit) do\n        if type(slot) == \"table\" and type(slot.export) == \"table\" and slot.getElementClass then\n            local elementClass = slot.getElementClass():lower()\n            if elementClass == \"screenunit\" then\n                system.print(\"getScreen: found screen\") \n                return slot\n            end\n        end\n    end\n    return false\nend\n\nfunction getNamedElement(_class, _name)\n    if gDebug == true then system.print(\"getNamedElement: looking for \" .. _name .. \"(\" .. _class .. \")\") end\n    \n    for slot_name, slot in pairs(unit) do\n        if type(slot) == \"table\" and type(slot.export) == \"table\" and slot.getElementClass then\n            local elementClass = slot.getElementClass()\n            if gDebug == true then system.print(\"getNamedElement: elementCla